import React, { Component } from "react";
import anime from "animejs";
import "./Curriculum.scss";

class Curriculum extends Component {
  state = {
    sceneOld: 0,
    scene: 0
  };

  constructor() {
    super();
  }
  componentDidMount() {
    // anime({
    //   targets: ".css-selector-demo .el",
    //   translateX: window.innerWidth
    // });
  }

  letScene = nb => {
    let animation1 = null;
    let animation2 = null;

    if (nb == 1) {
      let animation1index = anime({
        targets: "#content2",
        duration: 0,
        zIndex: 0
      });
      anime({ targets: "#content1", zIndex: 2 });
      animation1 = anime({
        targets: "#content1",
        translateX: 1200,
        duration: 700,
        easing: "linear",
        complete: function() {
          anime({ targets: "#content2", translateX: -1200, duration: 0 });
        }
      });
    }

    if (nb == 2) {
      let animation2index = anime({
        targets: "#content1",
        duration: 0,
        zIndex: 0
      });
      anime({ targets: "#content2", zIndex: 2 });
      animation2 = anime({
        targets: "#content2",
        translateX: 1200,
        duration: 700,
        easing: "linear",
        zIndex: 2,
        complete: function() {
          anime({ targets: "#content1", translateX: -1200, duration: 0 });
        }
      });
    }
  };

  render() {
    let { scene, sceneOld, sceneNew } = this.state;
    return (
      <div id="wrapper_curriculum">
        <div className="rect1">
          {/* <div className="textBox"> */}
          <div
            className="titreCv"
            onMouseEnter={() => {
              this.setState({ sceneOld: scene, scene: 1 }, () =>
                this.letScene(1)
              );
            }}
          >
            École 42
          </div>
          <div
            className="titreCv"
            onMouseEnter={() => {
              this.setState({ sceneOld: scene, scene: 2 }, () =>
                this.letScene(2)
              );
            }}
          >
            Centrale
          </div>
          {/* </div> */}
        </div>

        {(scene === 1 || sceneOld === 1) && (
          <div id="content1" className="contentCv">
            <div className="Filtre"></div>
            <div className="textRT">Projet RT</div>
            <div className="RectangleHaut"></div>
            <div className="textInRT">
              Développement d’un moteur graphique de type raytracing en C.
            </div>
            <div className="RectangleBas"></div>
            <div className="scroll_down"></div>
          </div>
        )}

        {(scene === 2 || sceneOld === 2) && (
          <div id="content2" className="contentCv">
            <div className="Filtre"></div>
            <div className="textRT">Projet RT CONTENT 2</div>
            <div className="RectangleHaut"></div>
            <div className="textInRT">
              Développement d’un moteur graphique de type raytracing en C.
            </div>
            <div className="RectangleBas"></div>
            <div className="scroll_down"></div>
          </div>
        )}
      </div>
    );
  }
}

export default Curriculum;
