import React, { Component } from "react";
import anime from "animejs";
import "./Curriculum.scss";

let a = null;
let b = null;
let c = null;
let d = null;

class Curriculum extends Component {
  state = {
    sceneOld: 0,
    scene: 0
  };

  constructor() {
    super();
  }
  componentDidMount() {
    // anime({
    //   targets: ".css-selector-demo .el",
    //   translateX: window.innerWidth
    // });
  }

  letScene = nb => {
    if (nb == 1) {
      a = anime({
        targets: "#content1",
        translateX: 1000,
        duration: 200,
        easing: "spring(1, 100, 100,0)",
        begin: function(anim) {
          anime({ targets: "#content1", zIndex: 2 });
          anime({ targets: "#content2", zIndex: 0 });
        },
        complete: () => {
          this.setState({ sceneOld: 1 });
        }
      });
      anime({
        targets: ".textInRT",
        fontSize: [0, 30],
        duration: 400
      });
    }

    if (nb == 2) {
      c = anime({
        targets: "#content2",
        translateX: 1000,
        duration: 200,
        easing: "spring(1, 100, 100,0)",
        begin: function(anim) {
          anime({ targets: "#content2", zIndex: 2 });
          anime({ targets: "#content1", zIndex: 0 });
        },
        complete: () => {
          this.setState({ sceneOld: 2 });
        }
      });
    }
  };

  render() {
    let { scene, sceneOld, sceneNew } = this.state;
    return (
      <div id="wrapper_curriculum">
        <div className="rect1">
          {/* <div className="textBox"> */}
          <div
            className="titreCv"
            onMouseEnter={() => {
              this.setState({ scene: 1 }, () => this.letScene(1));
            }}
          >
            École 42
          </div>
          <div
            className="titreCv"
            onMouseEnter={() => {
              this.setState({ scene: 2 }, () => this.letScene(2));
            }}
          >
            Centrale
          </div>
          {/* </div> */}
        </div>

        {scene === 1 && (
          <div id="content1" className="contentCv">
            <div className="Filtre"></div>
            <div className="textRT"></div>
            <div className="RectangleHaut"></div>
            <div className="textInRT">
              Développement d’un moteur graphique de type raytracing en C.
            </div>
            <div className="RectangleBas"></div>
            <div className="scroll_down"></div>
          </div>
        )}

        {scene === 2 && (
          <div id="content2" className="contentCvEcn">
            <div className="Filtre"></div>
            <div className="textRT"></div>
            <div className="RectangleHaut"></div>
            <div className="textInRT">Deuxieme page</div>
            <div className="RectangleBas"></div>
            <div className="scroll_down"></div>
          </div>
        )}
      </div>
    );
  }
}

export default Curriculum;
