import React, { Component } from "react";
import anime from "animejs";
import "./Curriculum.scss";
import Card from "@material-ui/core/Card";

import Particles from "react-particles-js";

import Polaroid from "../components/Card/Polaroid";
import PolaroidVideo from "../components/Card/PolaroidVideo";

import CvText from "../components/Card/CvText";

import Section00 from "./section0/Section00";
import Section01 from "./section0/Section01";
import Section02 from "./section0/Section02";

let lastCall = 0;

function play() {
  var blue = "#2980b9";
  var l = document.getElementById("logo");

  console.log("Lancement");
  setTimeout(function() {
    // modify this one line below, and see the result !
    let logoTitle;
    logoTitle[0] = " Une formation de qualité";
    logoTitle[1] = " autre Truc!!";
    var logoRandom = "";
    console.log(l);
    var logoTitleContainer = l.innerText;
    var possible = '-+*/|}{[]~\\":;?/.><=+-_)(*&^%$#@!)}';

    function generateRandomTitle(i, logoRandom) {
      setTimeout(function() {
        l.innerText = logoRandom;
      }, i * 70);
    }

    for (var k = 0; k < logoTitle.length; k++) {
      for (var i = 0; i < logoTitle[k].length + 1; i++) {
        logoRandom = logoTitle[k].substr(0, i);
        for (var j = i; j < logoTitle[k].length; j++) {
          logoRandom += possible.charAt(
            Math.floor(Math.random() * possible.length)
          );
        }
        generateRandomTitle(i, logoRandom);
        logoRandom = "";
      }
    }
  }, 500);
}

function throttle(func, wait, options) {
  var context, args, result;
  var timeout = null;
  var previous = 0;
  if (!options) options = {};
  var later = function() {
    previous = options.leading === false ? 0 : Date.now();
    timeout = null;
    result = func.apply(context, args);
    if (!timeout) context = args = null;
  };
  return function() {
    var now = Date.now();
    if (!previous && options.leading === false) previous = now;
    var remaining = wait - (now - previous);
    context = this;
    args = arguments;
    if (remaining <= 0 || remaining > wait) {
      if (timeout) {
        clearTimeout(timeout);
        timeout = null;
      }
      previous = now;
      result = func.apply(context, args);
      if (!timeout) context = args = null;
    } else if (!timeout && options.trailing !== false) {
      timeout = setTimeout(later, remaining);
    }
    return result;
  };
}

