      duration: 1000,
      minWidth: ["15%", "40%"],
      fontSize: "4vw",
      width: ["15%", "40%"],
      easing: "spring(2, 100, 100, 1)"
    });
    anime({
      targets: ".textCard",
      duration: 1000,
      height: ["90%", "70%"],
      easing: "spring(2, 100, 100, 1)"
    });
    this.setState({ screen: "60%" });
  };

  scrollEvent0 = event => {
    let timeout = 250;

    if (new Date() - lastCall < timeout) {
      return null;
    } else {
      lastCall = new Date();
      let section = this.state.section;
      if (event.deltaY > 0) {
        if (document.querySelector("#section0_" + (section + 1))) {
          this.setState({ section: section + 1 }, () => {
            let section = this.state.section;
            anime({
              targets: ".section",
              translateY: -window.innerHeight * section,
              duration: 300,
              easing: "spring(1, 100, 100,0)"
            });
          });
        }
      } else {
        if (document.querySelector("#section0_" + (section - 1))) {
          this.setState({ section: section - 1 }, () => {
            let section = this.state.section;
            anime({
              targets: ".section",
              translateY: -window.innerHeight * section,
              duration: 300,
              easing: "spring(1, 100, 100,0)"
            });
          });
        }
      }
    }
  };

  scrollEvent = event => {
    throttle(this.scrollEvent1(event), 400000);
  };

  scrollEvent1 = event => {
    let section = this.state.section;

    if (event.deltaY > 0) {
      this.setState({ section: section + 1 });
      if (section === 0) {
        anime({
          targets: "#section1_0",
          height: "100vh",
          width: "100%",

          easing: "spring(1, 100, 100,0)"
        });

        anime({
          targets: "#section1_1",
          opacity: [0, 1],

          easing: "spring(2, 100, 100,0)"
        });

        anime({
          targets: ".word1",
          opacity: [0, 1],

          easing: "spring(1, 100, 100,0)",
          complete: function() {
            play();
          }
        });
        // anime({
        //   targets: ".word2",
        //   opacity: [0, 1],
        //   duration: 30000
        //   //easing: "spring(20, 100, 100,0)"
        // });
        // anime({
        //   targets: ".word3",
        //   opacity: [0, 1],
        //   duration: 30000
        //   //easing: "spring(20, 100, 100,0)"
        // });
      } else if (section === 1) {
        anime({
          targets: ".specialite_texte",
          fontSize: "50px",
          fontWeight: 900,
          duration: 500
          //easing: "spring(1, 100, 100,0)"
        });
        anime({
          targets: ".specialite_wrapper",
          top: "15%",
          duration: 500
          //easing: "spring(1, 100, 100,0)"
        });
      }
    } else {
      this.setState({ section: section - 1 });
      if (section === 1) {
        anime({
          targets: "#section1_0",
          height: "auto",
          width: "100%",
          duration: 300
          // easing: "spring(1, 100, 100,0)"
        });
        anime({
          targets: "#section1_1",
          opacity: [1, 0],
          duration: 300
          //easing: "spring(10, 100, 100,0)"
        });
        anime({
          targets: ".word1",
          opacity: [1, 0]
          //easing: "spring(100, 100, 100,0)"
        });
        anime({
          targets: ".word2",
          opacity: [1, 0]
          //easing: "spring(100, 100, 100,0)"
        });
        anime({
          targets: ".word3",
          opacity: [1, 0]
          // easing: "spring(100, 100, 100,0)"
        });
      } else if (section === 2) {
        anime({
          targets: ".specialite",
          fontSize: "40px",
          fontWeight: 500,
          duration: 500,
          easing: "spring(1, 100, 100,0)"
        });
      }
    }
  };

  letScene = nb => {
    if (nb === 1) {
      document.querySelector("#content0").style.zIndex = "2";
      document.querySelector("#content1").style.zIndex = "0";
      document.querySelector("#content2").style.zIndex = "0";

      anime({
        targets: ".section",
        translateY: 0,
        duration: 0
      });

      anime({
        targets: "#content0",
        translateX: [-1000, 0],
        duration: 300,
        //width: ["40%", "60%"],
        easing: "spring(1, 100, 100,0)"
      });
    }

    if (nb === 2) {
      document.querySelector("#content1").style.zIndex = "2";
      document.querySelector("#content0").style.zIndex = "0";
      document.querySelector("#content2").style.zIndex = "0";
      anime({
        targets: "#content1",
        translateX: [-1000, 0],
        duration: 300,
        //width: ["40%", "60%"],
        easing: "spring(1, 100, 100,0)"
      });
    }

    if (nb === 3) {
      document.querySelector("#content2").style.zIndex = "2";
      document.querySelector("#content0").style.zIndex = "0";
      document.querySelector("#content1").style.zIndex = "0";
      anime({
        targets: "#content2",
        translateX: [-1000, 0],
        duration: 300,
        //width: ["40%", "60%"],
        easing: "spring(1, 100, 100,0)"
      });
    }
  };

  mySectionParticle = nb => {
    let particleWidth = this.state.scene === 0 ? window.innerWidth : "100%";

    return (
      <Particles
        height={window.innerHeight}
        width={particleWidth}
        className="particle"
        params={{
          particles: {
            number: {
              density: { enable: true, value_area: 700 }
            }
          },
          opacity: {
            value: 0.9,
            random: false,
            anim: {
              enable: false,
              speed: 1,
              opacity_min: 0.1,
              sync: false
            }
          },
          interactivity: {
            detect_on: "windows",
            events: {
              onhover: {
                enable: true,
                mode: "repulse"
              },
              onclick: {
                enable: true,
                mode: "push"
              },
              resize: true
            }
          }
        }}
      />
    );
  };

  render() {
    let { scene } = this.state;
    return (
      <div id="wrapper_curriculum">
        {this.mySectionParticle(this.state.section)}
        <div
          className="rect1"
          onMouseLeave={() => {
            this.blurAllExept(scene - 1);
          }}
        >
          {this.mySectionParticle(this.state.section)}
          <div
            className="titreCv"
            id="titre0"
            onClick={() => {
              if (scene !== 1) {
                this.setState({ scene: 1, section: 0 }, () => this.letScene(1));
              }
            }}
            onMouseEnter={() => {
              this.blurAllExept(0);
            }}
          >
            <span>École 42</span>
          </div>
          <div
            className="titreCv"
            id="titre1"
            onClick={() => {
              if (scene !== 2) {
                this.setState({ scene: 2, section: 0 }, () => this.letScene(2));
              }
            }}
            onMouseEnter={() => {
              this.blurAllExept(1);
            }}
          >
            <div className="titre_texte">Centrale Nantes</div>
          </div>
          <div
            className="titreCv"
            id="titre2"
            onClick={() => {
              if (scene !== 3) {
                this.setState({ scene: 3, section: 0 }, () => this.letScene(3));
              }
            }}
            onMouseEnter={() => {
              this.blurAllExept(2);
            }}
          >
            <div>CAPES mathématiques</div>
          </div>
        </div>

        <div
          id="content0"
          className="contentCv contentCv_wrapper"
          onClick={this.onClickScreen}
          onWheel={this.scrollEvent0}
        >
          <div className="Filtre"></div>

          <Section00 />
          <Section01 />
          <Section02 />
        </div>

        <div
          id="content1"
          className="contentCvEcn contentCv_wrapper"
          onClick={this.onClickScreen}
          onWheel={this.scrollEvent}
        >
          <section id="section1_0" className="section1">
            <Card id="bgEcn">
              <div className="Filtre"></div>
              <h1>Ingenieur généraliste</h1>
              <div className="specialite_wrapper">
                <h3 className="specialite_texte">
                  Spécialité Management de Projet et Energétique
                </h3>
              </div>
            </Card>
          </section>

          <section
            id="section1_1"
            className="section1"
            style={{ opacity: "0", width: "100%", position: "absolute" }}
          >
            <img
              src={require("./Ecn_section1.jpg")}
              style={{ width: "100%" }}
            />

            <div id="container"></div>

            <div
              className="word1 word typewriter"
              id="logo"
              style={{
                position: "absolute",
                top: "40%",
                background: "white"
              }}
            ></div>
            <div
              className="word2 word"
              style={{ position: "absolute", top: "20%" }}
            >
              Une sélection difficile
            </div>
            <div
              className="word3 word"
              style={{ position: "absolute", top: "70%", left: "30%" }}
            >
              Une renomée international
            </div>
          </section>
        </div>

        <div
          id="content2"
          className="contentCvTeacher contentCv_wrapper"
          onClick={this.onClickScreen}
        >
          <div className="Filtre"></div>
          <Card className="textCard">
            <div className="textInRT">Enseignant collége et lycée</div>
            <div className="scroll_down"></div>
          </Card>
        </div>

        <div
          id="contentDefault"
          className="contentDefault contentCv_wrapper"
        ></div>
      </div>
    );
  }
}

export default Curriculum;
