import React, { Component } from "react";
import anime from "animejs";
import "./Curriculum.scss";

class Curriculum extends Component {
  state = {
    scene: 0
  };

  constructor() {
    super();
  }
  componentDidMount() {
    // anime({
    //   targets: ".css-selector-demo .el",
    //   translateX: window.innerWidth
    // });
  }

  letScene = nb => {
    console.log("ANIMATION PLAY");
    let animation = anime({
      targets: ".content1",
      // Properties
      translateX: 1000,
      // Property Parameters
      duration: 500,
      easing: "linear"
      // Animation Parameters
    });
  };

  render() {
    let scene = this.state.scene;
    return (
      <div id="wrapper_curriculum">
        <div className="rect1">
          <div className="textBox">
            <div
              className="titreCv"
              onMouseEnter={() => {
                this.setState({ scene: 1 }, () => this.letScene(1));
              }}
            >
              École 42
            </div>
          </div>
        </div>

        {scene === 1 && (
          <div id="content1">
            <div className="Filtre"></div>
            <div className="textRT">Projet RT</div>
            <div className="textInRT">
              Développement d’un moteur graphique de type raytracing en C.
            </div>
            <div className="scroll_down"></div>
          </div>
        )}
      </div>
    );
  }
}

export default Curriculum;
