import React, { Component } from "react";
import anime from "animejs";
import "./Curriculum.scss";

let a = null;
let b = null;
let c = null;
let d = null;

class Curriculum extends Component {
  state = {
    sceneOld: 0,
    scene: 0
  };

  constructor() {
    super();
  }
  componentDidMount() {
    // anime({
    //   targets: ".css-selector-demo .el",
    //   translateX: window.innerWidth
    // });
  }

  letScene = nb => {
    if (nb == 1) {
      if (a !== null) {
        a.stop();
      }
      if (b !== null) {
        b.stop();
      }
      a = anime({
        targets: "#content1",
        translateX: 1000,
        duration: 700,
        easing: "linear",
        begin: function(anim) {
          anime({ targets: "#content1", zIndex: 2 });
        }
      });
      b = anime({
        targets: "#content2",
        translateX: -1000,
        delay: 700,
        begin: function(anim) {
          anime({ targets: "#content2", zIndex: 0 });
        }
      });
    }

    if (nb == 2) {
      if (c !== null) {
        c.stop();
      }
      if (d !== null) {
        d.stop();
      }
      c = anime({
        targets: "#content2",
        translateX: 1000,
        duration: 700,
        easing: "linear",
        begin: function(anim) {
          anime({ targets: "#content2", zIndex: 2 });
        }
      });
      d = anime({
        targets: "#content1",
        translateX: -1000,
        delay: 700,
        begin: function(anim) {
          anime({ targets: "#content1", zIndex: 0 });
        }
      });
    }
  };

  render() {
    let { scene, sceneOld, sceneNew } = this.state;
    return (
      <div id="wrapper_curriculum">
        <div className="rect1">
          {/* <div className="textBox"> */}
          <div
            className="titreCv"
            onMouseEnter={() => {
              this.setState({ sceneOld: scene, scene: 1 }, () =>
                this.letScene(1)
              );
            }}
          >
            École 42
          </div>
          <div
            className="titreCv"
            onMouseEnter={() => {
              this.setState({ sceneOld: scene, scene: 2 }, () =>
                this.letScene(2)
              );
            }}
          >
            Centrale
          </div>
          {/* </div> */}
        </div>

        {(scene === 1 || sceneOld === 1) && (
          <div id="content1" className="contentCv">
            <div className="Filtre"></div>
            <div className="textRT">Projet RT</div>
            <div className="RectangleHaut"></div>
            <div className="textInRT">
              Développement d’un moteur graphique de type raytracing en C.
            </div>
            <div className="RectangleBas"></div>
            <div className="scroll_down"></div>
          </div>
        )}

        {(scene === 2 || sceneOld === 2) && (
          <div id="content2" className="contentCv">
            <div className="Filtre"></div>
            <div className="textRT">Projet RT CONTENT 2</div>
            <div className="RectangleHaut"></div>
            <div className="textInRT">
              Développement d’un moteur graphique de type raytracing en C.
            </div>
            <div className="RectangleBas"></div>
            <div className="scroll_down"></div>
          </div>
        )}
      </div>
    );
  }
}

export default Curriculum;
