import React, { Component } from "react";
import anime from "animejs";
import "./Curriculum.scss";

class Curriculum extends Component {
  state = {
    sceneOld: 0,
    scene: 0
  };

  constructor() {
    super();
  }
  componentDidMount() {
    // anime({
    //   targets: ".css-selector-demo .el",
    //   translateX: window.innerWidth
    // });
  }

  letScene = nb => {
    if (nb == 1) {
      let animation = anime({
        targets: "#content1",
        translateX: 1200,
        duration: 700,
        easing: "linear"
      });
    }

    if (nb == 1) {
      let animation = anime({
        targets: "#content2",
        translateX: 1200,
        duration: 700,
        easing: "linear"
      });
    }
  };

  render() {
    let { scene, sceneOld, sceneNew } = this.state;
    return (
      <div id="wrapper_curriculum">
        <div className="rect1">
          {/* <div className="textBox"> */}
          <div
            className="titreCv"
            onMouseEnter={() => {
              this.setState({ sceneOld: 1 }, () => this.letScene(1));
            }}
          >
            École 42
          </div>
          <div
            className="titreCv"
            onMouseEnter={() => {
              this.setState({ scene: 2 }, () => this.letScene(1));
            }}
          >
            Centrale
          </div>
          {/* </div> */}
        </div>

        {scene === 1 && (
          <div id="content1" className="contentCv">
            <div className="Filtre"></div>
            <div className="textRT">Projet RT</div>
            <div className="RectangleHaut"></div>
            <div className="textInRT">
              Développement d’un moteur graphique de type raytracing en C.
            </div>
            <div className="RectangleBas"></div>
            <div className="scroll_down"></div>
          </div>
        )}

        {scene === 2 && (
          <div id="content2" className="contentCv">
            <div className="Filtre"></div>
            <div className="textRT">Projet RT</div>
            <div className="RectangleHaut"></div>
            <div className="textInRT">
              Développement d’un moteur graphique de type raytracing en C.
            </div>
            <div className="RectangleBas"></div>
            <div className="scroll_down"></div>
          </div>
        )}
      </div>
    );
  }
}

export default Curriculum;
