import React, { Component } from "react";
import { Row, Col, Image } from "react-bootstrap";
import DefaultImg from "./default-user-avatar.png";
import DeletePost from "./DeletePost";
import "./Post.css";
import Cookies from "universal-cookie";
import Hidden from "@material-ui/core/Hidden";
import Button from "@material-ui/core/Button";
import { Card, CardHeader, CardContent, Avatar, Grid } from "@material-ui/core";

import BestAnswer from "../components/BestAnswer";

const cookies = new Cookies();

class Post extends Component {
  state = {
    answer: null
  };

  constructor(props) {
    super(props);
    this.user = cookies.get("user");
  }

  componentDidMount() {
    this.getAnswerFromDb();
  }

  componentDidUpdate(nextProps) {
    if (nextProps.id !== this.props.id) {
      this.getAnswerFromDb();
    }
  }

  compareValues = (key, order = "asc") => {
    return function innerSort(a, b) {
      if (!a.hasOwnProperty(key) || !b.hasOwnProperty(key)) {
        // property doesn't exist on either object
        return 0;
      }

      const varA = typeof a[key] === "string" ? a[key].toUpperCase() : a[key];
      const varB = typeof b[key] === "string" ? b[key].toUpperCase() : b[key];

      let comparison = 0;
      if (varA > varB) {
        comparison = 1;
      } else if (varA < varB) {
        comparison = -1;
      }
      return order === "desc" ? comparison * -1 : comparison;
    };
  };

  getAnswerFromDb = () => {
    let url = "/api/best_answer/" + this.props.id;
    let req = new Request(url, {
      method: "GET",
      cache: "default",
      credentials: "include"
    });
    fetch(req)
      .then(res => {
        if (res.status === 401) {
          console.log("answer n'a pas recu la meilleur réponse].");
        } else {
          return res.json();
        }
      })
      .then(answer => {
        if (answer.length > 0) {
          answer.sort(this.compareValues("likes", "desc"));
          this.setState({
            answer: answer[0]
          });
        }
      });
  };

  onClickLike = () => {
    fetch("/api/likepost", {
      method: "POST",
      body: JSON.stringify({ msgId: this.props.id, user: this.user }),
      credentials: "include",
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(response => {
        if (response.status === 401) {
          console.log("Probleme de like");
        } else {
          this.props.refresh();
        }
      })
      .catch(error => console.error("Error:", error));
  };

  onClickDislike = () => {
    fetch("/api/dislikepost", {
      method: "POST",
      body: JSON.stringify({ msgId: this.props.id, user: this.user }),
      credentials: "include",
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(response => {
        if (response.status === 401) {
          console.log("Probleme de dislike");
        } else {
          this.props.refresh();
        }
      })
      .catch(error => console.error("Error:", error));
  };

  renderLikeDislike = () => {
    return (
      <div>
        <div>
          <Col>
            <Image
              className="banniere"
              src={require("./like.png")}
              width="20px"
              height="20px"
              style={{ cursor: "pointer" }}
              onClick={this.onClickLike}
            />
          </Col>
          <Col>
            <b>&nbsp;{this.props.likes}</b>
          </Col>
        </div>
        <div>
          <Col>
            <Image
              className="banniere"
              src={require("./dislike.png")}
              width="20px"
              height="20px"
              style={{ cursor: "pointer" }}
              onClick={this.onClickDislike}
            />
          </Col>
          <Col>
            <b>&nbsp;{this.props.dislikes}</b>
          </Col>
        </div>
      </div>
    );
  };

  render() {
    let texte = this.props.texte
      .split("\n")
      .map((item, i) => <p key={i}>{item}</p>);
    let success = false;
    let answer = this.state.answer;

    if (this.props.likes > this.props.dislikes) {
      success = true;
    }

    return (
      <Card className="m-4">
        <CardContent>
          <Row>
            <Col id="avatar_wrapper" className="col-auto">
              <Avatar src="/broken-image.jpg" />
            </Col>
            <Col className="border-right col1st  col-auto">
              <Row className="h-100">
                <Col id="like_dislike_wrapper">{this.renderLikeDislike()}</Col>
              </Row>
            </Col>
            <Col id="post_content_wrapper" className="border-dark">
              <CardHeader title={this.props.user + " " + this.props.date} />
              {texte}
              {this.props.user === localStorage.getItem("pseudo") && (
                <Col id="delete_wrapper" className="p-2 border-dark delete">
                  <DeletePost
                    id={this.props.id}
                    refresh={this.props.refresh}
                    editfunction={this.props.editfunction}
                    width="50%"
                  />
                </Col>
              )}
            </Col>
          </Row>

          {this.props.onMessageClick && (
            <Row>
              <div classname="repondre_wrapper">
                <Button
                  color="primary"
                  variant="contained"
                  size="small"
                  onClick={() => {
                    this.props.onMessageClick(this.props.id);
                  }}
                >
                  Répondre
                </Button>
              </div>
              {answer && (
                <Post
                  {...this.props}
                  user={answer.user}
                  texte={answer.texte}
                  date={answer.date}
                />
              )
              // && (
              //   <BestAnswer
              //     user={answer.user}
              //     texte={answer.texte}
              //     date={answer.date}
              //   />
              // )
              }
            </Row>
          )}
        </CardContent>
      </Card>
    );
  }
}

export default Post;
