import React from "react";
import "./Section10.scss";

function Section10() {
  window.onload = function() {
    var tl = new TimelineLite({ delay: 1 }),
      firstBg = document.querySelectorAll(".text__first-bg"),
      secBg = document.querySelectorAll(".text__second-bg"),
      word = document.querySelectorAll(".text__word");

    tl.to(firstBg, 0.2, { scaleX: 1 })
      .to(secBg, 0.2, { scaleX: 1 })
      .to(word, 0.1, { opacity: 1 }, "-=0.1")
      .to(firstBg, 0.2, { scaleX: 0 })
      .to(secBg, 0.2, { scaleX: 0 });

    document.querySelector(".restart").onclick = function() {
      tl.restart();
    };
  };
  return (
    <div className="text">
      <span className="text__first"></span>
      <span className="text__word">Hello</span>
      <span className="text__first-bg"></span>
      <br />
      <span className="text__second"></span>
      <span className="text__word">Hello</span>
      <span className="text__second-bg"></span>

      {/* button.restart repeat */}
    </div>
  );
}

export default Section10;
