import React from "react";
import "./Polaroid.css";

function Polaroid({ texte, image, style }) {
  return (
    <div className="polaroid" style={style}>
      <img
        src={image}
        alt="Castle"
        style={{ width: "13vw", borderRadius: "8px" }}
      />
      <div className="container">
        {typeof text === "string" && <p>{texte}</p>}
        {typeof text === "object" &&
          texte.map((line, index) => {
            return <p>{line}</p>;
          })}
      </div>
    </div>
  );
}

export default Polaroid;
