import React, { Component } from "react";
import anime from "animejs";
import "./Curriculum.scss";
import Card from "@material-ui/core/Card";
import * as Scroll from "react-scroll";
import Particles from "react-particles-js";

var scrollSpy = Scroll.scrollSpy;

let newScrollPosition = 0;
let oldScrollPosition = 0;

function throttle(cb, timeout) {
  // You can rewrite this function by replacing the time limit with the
  // scroll pitch. I think that would be the best solution.
  // let delta = e.deltaY || e.detail || e.wheelDelta;
  // ... sum delta and call callback by the number of steps: accumulator/step

  let lastCall = 0;

  return function() {
    if (new Date() - lastCall > timeout) {
      lastCall = new Date();

      cb();
    }
  };
}

class Curriculum extends Component {
  state = {
    scene: 0,
    section: 1,
    screen: "60%"
  };

  constructor(props) {
    super(props);
    for (let i = 1; i < 4; i++) {
      this["contentRef" + i] = 1;
    }
  }

  componentDidMount() {
    window.addEventListener("wheel", this.scrollEvent.bind(this));
    //scrollSpy.update();
  }

  blurAllExept = nb => {
    let x = (nb + 1) % 3;
    let y = (nb + 2) % 3;
    var targetElm0 = document.querySelector("#titre" + nb);
    var targetElm1 = document.querySelector("#titre" + x);
    var targetElm2 = document.querySelector("#titre" + y);
    anime({
      targets: "#titre" + x + ",#titre" + y,
      duration: 200,
      update: function(anim) {
        targetElm0.style.filter = "blur(0px)";
        targetElm1.style.filter = "blur(" + (10 * anim.progress) / 100 + "px)";
        targetElm2.style.filter = "blur(" + (10 * anim.progress) / 100 + "px)";
      }
    });
  };

  onClickScreen = () => {
    if (this.state.screen == "60%") {
      this.makeFullScreen();
    } else {
      this.makeNormalScreen();
    }
  };

  makeFullScreen = () => {
    this.setState({ screen: "85%" });
    anime({
      targets: ".contentCv_wrapper",
      duration: 1000,
      position: "absolute",
      width: ["60%", "85%"],
      left: "15%",
      easing: "spring(2, 100, 100, 1)"
    });
    anime({
      targets: ".rect1",
      duration: 1000,
      minWidth: ["40%", "15%"],
      fontSize: "1vw",
      width: ["40%", "15%"],
      easing: "spring(2, 100, 100, 1)"
    });
  };

  makeNormalScreen = () => {
    anime({
      targets: ".contentCv_wrapper",
      duration: 1000,
      position: "absolute",
      width: ["85%", "60%"],
      left: "40%",
      easing: "spring(2, 100, 100, 1)"
    });
    anime({
      targets: ".rect1",
      duration: 1000,
      minWidth: ["15%", "40%"],
      fontSize: "4vw",
      width: ["15%", "40%"],
      easing: "spring(2, 100, 100, 1)"
    });
    this.setState({ screen: "60%" });
  };

  scrollTo = (offset, callback) => {
    // window.removeEventListener("scroll", this.scrollEvent);
    const onScroll = () => {
      if (window.pageYOffset === offset) {
        console.log("ici");
        window.removeEventListener("scroll", onScroll);
        // window.addEventListener("scroll", this.scrollEvent);
        callback();
      }
    };

    window.addEventListener("scroll", onScroll);
    onScroll();
    window.scrollTo({
      top: offset,
      behavior: "smooth"
    });
  };

  scrollEvent = event => {
    //newScrollPosition = window.scrollY;
    console.log(event.deltaY);
    //window.removeEventListener("wheel", this.scrollEvent);
    let section = this.state.section;

    // if (newScrollPosition - oldScrollPosition > 0) {
    // if (
    //   this["contentRef" + (section + 1)] != null &&
    //   this["contentRef" + (section + 1)].current != null
    // ) {
    if (event.deltaY > 0) {
      if (this["contentRef" + (section + 1)]) {
        //console.log("#section" + (section + 1) + ",#section" + section);
        anime({
          targets: "#section" + (section + 1) + ",#section" + section,
          translateY: -window.innerHeight * section,
          duration: 300,
          easing: "spring(1, 100, 100,0)"
        });
      }
    } else {
      if (this["contentRef" + (section - 1)]) {
        //console.log("#section" + (section + 1) + ",#section" + section);
        anime({
          targets: "#section" + (section - 1) + ",#section" + section,
          translateY: window.innerHeight,
          duration: 300,
          easing: "spring(1, 100, 100,0)"
        });
      }
    }
    // this.scrollTo(
    //   this["contentRef" + (section + 1)].current.offsetTop,
    //   () => {
    //     this.setState({ section: section + 1 }, () => {
    //       oldScrollPosition = window.scrollY;
    //       //window.addEventListener("wheel", this.scrollEvent);
    //     });
    //   }
    // );
    // }
  };
  // } else if (newScrollPosition - oldScrollPosition < 0) {
  //   if (
  //     this["contentRef" + (section - 1)] != null &&
  //     this["contentRef" + (section - 1)].current != null
  //   ) {
  //     this.scrollTo(
  //       this["contentRef" + (section - 1)].current.offsetTop,
  //       () => {
  //         this.setState({ section: section - 1 }, () => {
  //           oldScrollPosition = window.scrollY;
  //           //window.addEventListener("wheel", this.scrollEvent);
  //         });
  //       }
  //     );
  //   }
  // }
  //};

  // scrollEvent = () => {
  //   newScrollPosition = window.scrollY;
  //   console.log("WHEEELLL " + (newScrollPosition - oldScrollPosition));
  //   //window.removeEventListener("wheel", this.scrollEvent);
  //   let section = this.state.section;

  //   if (newScrollPosition - oldScrollPosition > 0) {
  //     if (
  //       this["contentRef" + (section + 1)] != null &&
  //       this["contentRef" + (section + 1)].current != null
  //     ) {
  //       this.scrollTo(
  //         this["contentRef" + (section + 1)].current.offsetTop,
  //         () => {
  //           this.setState({ section: section + 1 }, () => {
  //             oldScrollPosition = window.scrollY;
  //             //window.addEventListener("wheel", this.scrollEvent);
  //           });
  //         }
  //       );
  //     }
  //   } else if (newScrollPosition - oldScrollPosition < 0) {
  //     if (
  //       this["contentRef" + (section - 1)] != null &&
  //       this["contentRef" + (section - 1)].current != null
  //     ) {
  //       this.scrollTo(
  //         this["contentRef" + (section - 1)].current.offsetTop,
  //         () => {
  //           this.setState({ section: section - 1 }, () => {
  //             oldScrollPosition = window.scrollY;
  //             //window.addEventListener("wheel", this.scrollEvent);
  //           });
  //         }
  //       );
  //     }
  //   }
  // };

  letScene = nb => {
    if (nb == 1) {
      document.querySelector("#content1").style.zIndex = "2";
      document.querySelector("#content2").style.zIndex = "0";
      document.querySelector("#content3").style.zIndex = "0";
      anime({
        targets: "#content1",
        translateX: [-1000, 0],
        duration: 300,
        //width: ["40%", "60%"],
        easing: "spring(1, 100, 100,0)"
      });
    }

    if (nb == 2) {
      document.querySelector("#content2").style.zIndex = "2";
      document.querySelector("#content1").style.zIndex = "0";
      document.querySelector("#content3").style.zIndex = "0";
      anime({
        targets: "#content2",
        translateX: [-1000, 0],
        duration: 300,
        //width: ["40%", "60%"],
        easing: "spring(1, 100, 100,0)"
      });
    }

    if (nb == 3) {
      document.querySelector("#content3").style.zIndex = "2";
      document.querySelector("#content1").style.zIndex = "0";
      document.querySelector("#content2").style.zIndex = "0";
      anime({
        targets: "#content3",
        translateX: [-1000, 0],
        duration: 300,
        //width: ["40%", "60%"],
        easing: "spring(1, 100, 100,0)"
        // begin: function(anim) {
        //   document.querySelector("#content3").style.zIndex = "2";
        //   document.querySelector("#content1").style.zIndex = "0";
        //   document.querySelector("#content2").style.zIndex = "0";
        // }
      });
    }
  };

  render() {
    let { scene, sceneOld, sceneNew } = this.state;
    let particleWidth = scene == 0 ? "window.innerWidth" : "100%";

    return (
      <div id="wrapper_curriculum">
        <div className="rect1">
          <Particles
            height={window.innerHeight}
            width={particleWidth}
            className="particle"
            params={{
              opacity: {
                value: 0.9,
                random: false,
                anim: {
                  enable: false,
                  speed: 1,
                  opacity_min: 0.1,
                  sync: false
                }
              },
              interactivity: {
                detect_on: "windows",
                events: {
                  onhover: {
                    enable: true,
                    mode: "repulse"
                  },
                  onclick: {
                    enable: true,
                    mode: "push"
                  },
                  resize: true
                }
              }
            }}
          />

          <div
            className="titreCv"
            id="titre0"
            onMouseEnter={() => {
              this.blurAllExept(0);
              if (scene !== 1) {
                this.setState({ scene: 1 }, () => this.letScene(1));
              }
            }}
          >
            École 42
          </div>
          <div
            className="titreCv"
            id="titre1"
            onMouseEnter={() => {
              this.blurAllExept(1);
              if (scene !== 2) {
                this.setState({ scene: 2 }, () => this.letScene(2));
              }
            }}
          >
            Centrale Nantes
          </div>
          <div
            className="titreCv"
            id="titre2"
            onMouseEnter={() => {
              this.blurAllExept(2);
              if (scene !== 3) {
                this.setState({ scene: 3 }, () => this.letScene(3));
              }
            }}
          >
            <div>CAPES</div>
            <div>mathématiques</div>
          </div>
        </div>

        <div
          id="content1"
          className="contentCv contentCv_wrapper"
          onClick={this.onClickScreen}
        >
          <div className="Filtre"></div>

          <section id="section1" ref={this.contentRef1}>
            <div className="wrapper_rt">
              <div className="image_rt"></div>
            </div>
            <Card className="textCard">
              <div className="textInRT">
                Développement d’un moteur graphique de type raytracing en C.
              </div>
              <div className="scroll_down"></div>
            </Card>
          </section>

          <section id="section2" ref={this.contentRef2}>
            <Card className="textCard">
              <div className="textInRT">2eme section</div>
              <div className="scroll_down"></div>
            </Card>
          </section>

          <section id="section3" ref={this.contentRef3}>
            <Card className="textCard">
              <div className="textInRT">3eme section</div>
              <div className="scroll_down"></div>
            </Card>
          </section>
        </div>

        <div
          id="content2"
          className="contentCvEcn contentCv_wrapper"
          onClick={this.onClickScreen}
        >
          <div className="Filtre"></div>
          <Card className="textCard" name="card2" id="card2">
            <div className="textInRT">Ingenieur généraliste</div>
            <div className="textInRT">
              Spécialité Énergie et Management de Projet
            </div>
            <div className="scroll_down"></div>
          </Card>
        </div>

        <div
          id="content3"
          className="contentCvTeacher contentCv_wrapper"
          onClick={this.onClickScreen}
        >
          <div className="Filtre"></div>
          <Card className="textCard">
            <div className="textInRT">Enseignant collége et lycée</div>
            <div className="scroll_down"></div>
          </Card>
        </div>

        <div
          id="contentDefault"
          className="contentDefault contentCv_wrapper"
        ></div>
      </div>
    );
  }
}

export default Curriculum;
